import logo from './logo.svg';
import './App.css';
import { Component } from 'react';

class Element extends Component { 
  constructor(props) {
    super(props);
  };
  componentDidUpdate(prevProps) {
    if (this.props.condition != prevProps.condition) { 
      var current_element = document.getElementById(this.props.id);
      if (this.props.condition == "default") { 
        current_element.style.background = '#FFFFFF';
      }
      else if (this.props.condition == "block") { 
        current_element.style.background = "#000000";
      }
      else if (this.props.condition == "considered") { 
        current_element.style.background = "#FFFF00";
      }
      else if (this.props.condition == "endpoint") { 
        current_element.style.background = "#33FFFF";
      }
      else if (this.props.condition == "chosen") { 
        current_element.style.background = "#00FF00";
      }
    }; 
  };
  render() { 
    return <button className="square" id = {this.props.id} onclick> {this.props.id} </button>;
  };
};

class Graph extends Component { 
  constructor(props) { 
    super(props); 
    this.state = { 
      adjacency_list: {},
      endpoints:[], 
      conditions:{}, 
    };
    this.changeCondition = this.changeCondition.bind(this)
    for (let i = 1; i <= 400; i++) {
      this.state.conditions[i] = "default"; 
    };
    /* TODO: initialize adjacency_list */
  };

  changeCondition(i) { 
    var new_conditions = this.state.conditions;
    var new_endpoints = this.state.endpoints;
    var new_adjacency_list = this.state.adjacency_list; 
    if (this.state.conditions[i] == "default") { 
      new_conditions[i] = "endpoint"; 
      new_endpoints.push(i) 
    }
    else if (this.state.condition[i] == "endpoint") { 
      new_conditions[i] = "block"; 
      new_endpoints.splice(new_endpoints.indexOf(i)); 
      /* TODO: change adjacency_list to remove i */
    }
    else if (condition[i] == "block") { 
      new_conditions[i] = "default"; 
      /* TOOD: change adjacency_list to add i  */
    }
    this.setState(state => ({ 
      conditions: new_conditions, 
      endpoints: new_endpoints,
      adjacency_list: new_adjacency_list, 
    })); 
  }; 

  renderElement(i) { 
    return <Element id={i} condition={this.state.conditions[i]} onClick={() => this.changeCondition(i)}/>
  }

  render() { 
    var board = []; 
    for (let i = 0; i <= 400; i++) { 
      board.push(this.renderElement(i)); 
      if (i % 20 == 1) { 
        board.push("<br>")
      } 
    }
    return board; 
  }
}


function App() {
  return (
    <div className="App">
    <Graph/>
    </div>
  );
}

export default App;
